package logo; import classes.EV3MovePilot; import java.util.concurrent.Callable;     import classes.*;public class Logo {         EV3MovePilot robot;     public Logo() {         this.robot = new EV3MovePilot();     }public void func1(DoubleVariable n, CallableVariable f)            throws ReturnException {                try {                    for(int i=0;i<n.value;i++) {                         f.value.call();                    }                 } catch (ReturnException e) {                    throw e;                } catch (Exception e) {                    System.out.println("An unidentified error occurred in repeat.");                }            } public void func2(DoubleVariable itr, DoubleVariable start,            DoubleVariable limit, DoubleVariable step, CallableVariable f) throws ReturnException {                try {                    for (itr.value = start.value; itr.value <= limit.value; itr.value+=step.value) {                        f.value.call();                    }                } catch (ReturnException e) {                    throw e;                } catch (Exception e) {                    System.out.println("An unidentified error occurred in for.");                }            } public void run() { DoubleVariable temp3 = new DoubleVariable(10.0); this.robot.travel(temp3.value); }public static void main(String[] args) { Logo logo = new Logo(); logo.run();} }